<?php

namespace Bav\Validator\De;

/**
 * Test class for System09.
 * Generated by PHPUnit on 2012-07-05 at 19:22:55.
 */
class System09Test extends SystemTestCase
{

    public function testWithAccountReturnsTrue()
    {
        $validAccounts = array('14564553', '539290858', '1000805', '539290859', '59999');

        foreach ($validAccounts as $account) {
            $validator = new System09($this->bankId);
            $this->assertTrue($validator->isValid($account));
        }
    }

}